CREATE OR REPLACE FUNCTION obter_lote_economico_mat ( cd_estabelecimento_p bigint, cd_cnpj_p text, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE

 
qt_compra_melhor_w		double precision;


BEGIN 
 
if (coalesce(cd_cnpj_p::text, '') = '') then 
	 
	select	coalesce(max(qt_compra_melhor),0) 
	into STRICT	qt_compra_melhor_w 
	from	material 
	where	cd_material = cd_material_p;
 
else 
	select	coalesce(obter_dados_material_fornec(cd_estabelecimento_p, cd_cnpj_p, cd_material_p, 1),0) 
	into STRICT	qt_compra_melhor_w 
	;
	 
	if (qt_compra_melhor_w = 0) then 
	 
		select	coalesce(max(qt_compra_melhor),0) 
		into STRICT	qt_compra_melhor_w 
		from	material 
		where	cd_material = cd_material_p;
	end if;
end if;
 
return	qt_compra_melhor_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
