CREATE OR REPLACE FUNCTION obter_lote_fornecedor_opm ( nr_lote_producao_p bigint, nr_seq_op_opm_p bigint ) RETURNS bigint AS $body$
DECLARE


nr_lote_fornecedor_w			bigint;
cd_lote_fornecedor_w			ordem_producao_opm.cd_lote_fornecedor%type;


BEGIN

select 	max(cd_lote_fornecedor)
into STRICT	cd_lote_fornecedor_w
from	ordem_producao_opm
where	nr_sequencia = nr_seq_op_opm_p;

if (nr_lote_producao_p IS NOT NULL AND nr_lote_producao_p::text <> '') then

	select	max(nr_sequencia)
	into STRICT	nr_lote_fornecedor_w
	from	material_lote_fornec
	where	nr_lote_producao = nr_lote_producao_p;
end if;


if (coalesce(nr_lote_fornecedor_w::text, '') = '') then

nr_lote_fornecedor_w := cd_lote_fornecedor_w;

end if;

return	nr_lote_fornecedor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
