CREATE OR REPLACE FUNCTION obter_lote_fornec_item_unit (nr_seq_item_unitarizacao_p bigint) RETURNS bigint AS $body$
DECLARE


nr_retorno_w	bigint;
nr_lote_fornec_w	bigint;


BEGIN

select	coalesce(nr_sequencia,0)
into STRICT	nr_lote_fornec_w
from	material_lote_fornec
where	nr_seq_item_unitarizacao =  nr_seq_item_unitarizacao_p;

if (nr_lote_fornec_w > 0) then
	nr_retorno_w := nr_lote_fornec_w;
end if;

return	nr_retorno_w;

end;
