CREATE OR REPLACE FUNCTION obter_lote_fornec_mat ( nr_seq_processo_p bigint, cd_material_p bigint, nr_seq_horario_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_processo_w 	bigint := nr_seq_processo_p;
ie_area_fornec_w	varchar(1);
cd_barras_frac_w	varchar(14);


BEGIN
select	substr(obter_codigo_barras_ordem_frac(nr_seq_etiqueta),1,14),
		substr(obter_se_area_lote_fornec(nr_seq_area_prep),1,1)
into STRICT		cd_barras_frac_w,
		ie_area_fornec_w
from		prescr_mat_hor
where	nr_sequencia = nr_seq_horario_p;


if (coalesce(nr_seq_processo_p::text, '') = '') or (nr_seq_processo_p = 0) then
	select	max(nr_seq_processo)
	into STRICT		nr_seq_processo_w
	from		prescr_mat_hor
	where	nr_sequencia = nr_seq_horario_p;
end if;

if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') and (ie_area_fornec_w = 'S') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	select	coalesce(lpad(nr_seq_lote_fornec,10,'0'),cd_material)
	into STRICT		ds_retorno_w
	from		adep_processo_item
	where	nr_seq_processo = nr_seq_processo_w
	and		nr_seq_horario = nr_seq_horario_p
	and		cd_material = cd_material_p;

else
	ds_retorno_w :=  cd_barras_frac_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
