CREATE OR REPLACE FUNCTION obter_lote_preparo_gedipa (nr_seq_processo_p bigint, nr_seq_area_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


nr_lote_producao_w	bigint;
ie_possui_material_w	varchar(1);
ds_lote_prod_w		varchar(15);



BEGIN
ds_lote_prod_w := '';

if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') and (nr_seq_area_p IS NOT NULL AND nr_seq_area_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	select	coalesce(max(a.nr_etiqueta),'')
	into STRICT	ds_lote_prod_w
	from	lote_producao_comp b,
		lp_individual a
	where	a.nr_lote_producao	=	b.nr_lote_producao
	and	a.nr_seq_area_prep 	=	nr_seq_area_p
	and	a.nr_seq_processo  	= 	nr_seq_processo_p
	and	obter_ficha_tecnica_medic(b.cd_material) = obter_ficha_tecnica_medic(cd_material_p);

end if;


return	ds_lote_prod_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
