CREATE OR REPLACE FUNCTION obter_lote_reagente (nr_seq_exame_lote_p bigint, nr_seq_exame_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(255);
nr_seq_lote_fornec_w	bigint;
ie_forma_apresentacao_w	varchar(1);
ds_lote_fornec_w	varchar(20);

c01 CURSOR FOR 
	SELECT	nr_seq_lote_fornec, 
		substr(Obter_dados_lote_fornec(nr_seq_lote_fornec,'D'),1,255) 
	from	san_exame_reagente_fornec 
	where	nr_seq_exame_lote = nr_seq_exame_lote_p 
	and	nr_seq_exame	= nr_seq_exame_p;


BEGIN 
 
ie_forma_apresentacao_w := Obter_Valor_Param_Usuario(450, 450, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento);
 
open c01;
loop 
fetch c01 into	 
	nr_seq_lote_fornec_w, 
	ds_lote_fornec_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	 
	if (ie_forma_apresentacao_w = 'D') then 
		ds_retorno_w	:= ds_retorno_w || '; ' || ds_lote_fornec_w;
	else 
		ds_retorno_w	:= ds_retorno_w || '; ' || nr_seq_lote_fornec_w;
	end if;
	 
	end;
end loop;
close c01;
 
ds_retorno_w	:= substr(ds_retorno_w,3,length(ds_retorno_w));
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
