SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_lote_recurso_nc (nr_sequencia_nc_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_lote_audit_w			lote_audit_hist.nr_seq_lote_audit%type;
ds_retorno_w				lote_audit_hist.nr_seq_lote_audit%type;
nr_seq_lote_audit_hist_w	nota_credito.nr_seq_lote_audit_hist%type;
nr_seq_lote_hist_guia_w		nota_credito.nr_seq_lote_hist_guia%type;

/*Essa function foi baseada no select  existente no atributo NR_SEQ_LOTE_AUDIT da tabela NOTA_CREDITO */

BEGIN

if (nr_sequencia_nc_p IS NOT NULL AND nr_sequencia_nc_p::text <> '') then

	select	max(a.nr_seq_lote_audit_hist),
			max(a.nr_seq_lote_hist_guia)
	into STRICT	nr_seq_lote_audit_hist_w,
			nr_seq_lote_hist_guia_w
	from	nota_credito a
	where	a.nr_sequencia = nr_sequencia_nc_p;

	if (nr_seq_lote_audit_hist_w IS NOT NULL AND nr_seq_lote_audit_hist_w::text <> '') then

		select	max(x.nr_seq_lote_audit)
		into STRICT	nr_seq_lote_audit_w
		from	lote_audit_hist x
		where	x.nr_sequencia	= nr_seq_lote_audit_hist_w;

		ds_retorno_w := nr_seq_lote_audit_w;

	end if;

	if (coalesce(nr_seq_lote_audit_w::text, '') = '') and (nr_seq_lote_hist_guia_w IS NOT NULL AND nr_seq_lote_hist_guia_w::text <> '') then /*se n√£o achou no primeiro select e possuir nr_seq_lote_hist_guia_w*/
		select	max(y.nr_seq_lote_audit)
		into STRICT	nr_seq_lote_audit_w
		from	lote_audit_hist y,
				lote_audit_hist_guia x
		where	x.nr_sequencia		= nr_seq_lote_hist_guia_w
		and		x.nr_seq_lote_hist	= y.nr_sequencia;

		ds_retorno_w := nr_seq_lote_audit_w;

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_lote_recurso_nc (nr_sequencia_nc_p bigint) FROM PUBLIC;

