CREATE OR REPLACE FUNCTION obter_macros_autocomplete (cd_funcao_p regra_macro.cd_funcao%type, cd_perfil_p regra_macro.cd_perfil%type, nm_usuario_p regra_macro.nm_usuario%type) RETURNS REGRA_MACRO.NR_SEQUENCIA%TYPE AS $body$
DECLARE


nr_sequencia_w	regra_macro.nr_sequencia%type := 0;


BEGIN
	
select	coalesce(max(a.nr_sequencia), 0)
into STRICT	nr_sequencia_w
from	regra_macro a
where	a.cd_funcao = cd_funcao_p
and		a.nm_usuario_regra = nm_usuario_p;

if (nr_sequencia_w = 0) THEN
	select	coalesce(max(b.nr_sequencia), 0)
	into STRICT	nr_sequencia_w
	from	regra_macro b
	where	b.cd_funcao = cd_funcao_p
	and 	b.cd_perfil = cd_perfil_p;
end if;

if (nr_sequencia_w = 0) THEN
	select	coalesce(max(b.nr_sequencia), 0)
	into STRICT	nr_sequencia_w
	from	regra_macro b
	where	b.cd_funcao = cd_funcao_p;
end if;

return nr_sequencia_w;

end;
$body$
