CREATE OR REPLACE FUNCTION obter_macros_envio_pj (ie_tipo_envio_p text) RETURNS varchar AS $body$
DECLARE



ds_retorno_w		varchar(300);
ds_enter_w		varchar(10) := chr(13) || chr(10);


BEGIN

if (ie_tipo_envio_p = 'A') then
	ds_retorno_w	:=	'@ds_historico = ' || wheb_mensagem_pck.get_texto(301582) || ds_enter_w || -- Histórico de alteração
