				'@usuario = ' || wheb_mensagem_pck.get_texto(301584) || ds_enter_w || --  Usuário que realizou a alteração
				'@razao_social = ' || wheb_mensagem_pck.get_texto(301585) || ds_enter_w || -- Razão social da pessoa jurídica
				'@perfil_ativo = ' || wheb_mensagem_pck.get_texto(301586) || ds_enter_w || -- Perfil logado ao gerar a comunicação
				'@tipo_pessoa = ' || wheb_mensagem_pck.get_texto(301599); -- Tipo de pessoa jurídica
elsif (ie_tipo_envio_p = 'I') then
	ds_retorno_w	:=	'@cnpj = ' || wheb_mensagem_pck.get_texto(301583) || ds_enter_w || -- Código do CNPJ da pessoa jurídica
				'@usuario = ' || wheb_mensagem_pck.get_texto(301600) || ds_enter_w || -- Usuário que inativou
				'@razao_social = ' || wheb_mensagem_pck.get_texto(301585) || ds_enter_w || -- Razão social da pessoa jurídica
				'@perfil_ativo = ' || wheb_mensagem_pck.get_texto(301586) || ds_enter_w || -- Perfil logado ao gerar a comunicação
				'@tipo_pessoa = ' || wheb_mensagem_pck.get_texto(301599); -- Tipo de pessoa jurídica
elsif (ie_tipo_envio_p = 'T') then
	ds_retorno_w	:=	'@cnpj = ' || wheb_mensagem_pck.get_texto(301583) || ds_enter_w || -- Código do CNPJ da pessoa jurídica
				'@usuario = ' || wheb_mensagem_pck.get_texto(301601) || ds_enter_w || -- Usuário que ativou
				'@razao_social = ' || wheb_mensagem_pck.get_texto(301585) || ds_enter_w || -- Razão social da pessoa jurídica
				'@perfil_ativo = ' || wheb_mensagem_pck.get_texto(301586) || ds_enter_w || -- Perfil logado ao gerar a comunicação
				'@tipo_pessoa = ' || wheb_mensagem_pck.get_texto(301599); -- Tipo de pessoa jurídica
elsif (ie_tipo_envio_p = 'C') then
	ds_retorno_w	:=	'@cnpj = ' || wheb_mensagem_pck.get_texto(301583) || ds_enter_w || -- Código do CNPJ da pessoa jurídica
				'@usuario = ' || wheb_mensagem_pck.get_texto(301602) || ds_enter_w || -- Usuário que criou
				'@razao_social = ' || wheb_mensagem_pck.get_texto(301585) || ds_enter_w || -- Razão social da pessoa jurídica
				'@perfil_ativo = ' || wheb_mensagem_pck.get_texto(301586) || ds_enter_w || -- Perfil logado ao gerar a comunicação
				'@tipo_pessoa = ' || wheb_mensagem_pck.get_texto(301599); -- Tipo de pessoa jurídica
end if;

return	ds_retorno_w;

end;
