CREATE OR REPLACE FUNCTION obter_macros_formula_compra ( nr_Sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_formula_w	varchar(30);
ds_calculo_w	varchar(1000);
ds_enter_w	varchar(15) := chr(13) || chr(10);
ds_retorno_w	varchar(4000);

C01 CURSOR FOR
SELECT	ds_formula,
	ds_calculo
from	sup_formula_compra
where	ie_situacao = 'A'
and	nr_sequencia <> nr_Sequencia_p;


BEGIN
ds_retorno_w := '@QT_SOLICITACAO_COMPRA@ = '|| wheb_mensagem_pck.get_texto(315124) || ds_enter_w ||
		'@QT_COTACAO_COMPRA@ = '|| wheb_mensagem_pck.get_texto(315126) || ds_enter_w ||
		'@QT_ORDEM_COMPRA@ = '||wheb_mensagem_pck.get_texto(315127) || ds_enter_w ||
		'@QT_SALDO_ESTOQUE@ = '||wheb_mensagem_pck.get_texto(315129) || ds_enter_w ||
		'@QT_CONSUMO_MENSAL@ = '||wheb_mensagem_pck.get_texto(315131)|| ds_enter_w ||
		'@QT_DIA_COMPRA@ = '||wheb_mensagem_pck.get_texto(315141) || ds_enter_w ||
		'@QT_DIA_FREQUENCIA@ = '||wheb_mensagem_pck.get_texto(315142) || ds_enter_w ||
		'@QT_ESTOQUE_MAXIMO@ = '||wheb_mensagem_pck.get_texto(315143) || ds_enter_w ||
		'@QT_ESTOQUE_MINIMO@ = '||wheb_mensagem_pck.get_texto(315145) || ds_enter_w ||
		'@QT_PONTO_PEDIDO@ = '||wheb_mensagem_pck.get_texto(315146)|| ds_enter_w ||
		'@QT_DIA_INTERV_RESSUP@ = '||wheb_mensagem_pck.get_texto(315148) || ds_enter_w ||
		'@QT_DIA_RESSUP_FORN@ = '||wheb_mensagem_pck.get_texto(315149)|| ds_enter_w ||
		'@QT_DIA_ESTOQUE_MINIMO@ = '||wheb_mensagem_pck.get_texto(315150) || ds_enter_w;

open C01;
loop
fetch C01 into
	ds_formula_w,
	ds_calculo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_retorno_w := substr(ds_retorno_w || '#'||ds_formula_w || wheb_mensagem_pck.get_texto(315151) || ds_calculo_w || '.' || ds_enter_w,1,4000);
	end;
end loop;
close C01;

return ds_retorno_w;

end;
$body$
