CREATE OR REPLACE FUNCTION obter_macro_compl_email_os ( ds_complemento_p text, nr_seq_ordem_p bigint) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
------------------------------------------------------------------------------------------------------------------- 
Referências: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
 
ds_retorno_w			varchar(4000)	:= '';
ds_dano_breve_w			man_ordem_servico.ds_dano_breve%type;
cd_pessoa_solicitante_w		man_ordem_servico.cd_pessoa_solicitante%type;
nr_seq_localizacao_w		man_ordem_servico.nr_seq_localizacao%type;
ds_solicitante_w		varchar(255);
ds_localizacao_w		varchar(80);


BEGIN 
if (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') and (ds_complemento_p IS NOT NULL AND ds_complemento_p::text <> '') then 
	select	max(a.ds_dano_breve), 
		max(a.cd_pessoa_solicitante), 
		max(a.nr_seq_localizacao) 
	into STRICT	ds_dano_breve_w, 
		cd_pessoa_solicitante_w, 
		nr_seq_localizacao_w 
	from	man_ordem_servico	a 
	where	a.nr_sequencia	= nr_seq_ordem_p;
	 
	ds_solicitante_w	:= coalesce(substr(obter_apelido_pessoa(cd_pessoa_solicitante_w),1,255), substr(obter_nome_pf(cd_pessoa_solicitante_w),1,255));
	ds_localizacao_w	:= substr(Obter_desc_man_localizacao(nr_seq_localizacao_w),1,80);
	 
	ds_retorno_w	:= substr(ds_complemento_p,1,4000);
 
	ds_retorno_w	:= replace_macro(ds_retorno_w, '@ds_dano_breve', ds_dano_breve_w);
	ds_retorno_w	:= replace_macro(ds_retorno_w, '@solicitante', ds_solicitante_w);
	ds_retorno_w	:= replace_macro(ds_retorno_w, '@localizacao', ds_localizacao_w);
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
