SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_macro_disp_regra_aniv (ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000);
ds_enter_w		varchar(10) := chr(13) || chr(10);


BEGIN

if (ie_tipo_p = 'T') then
	ds_retorno_w	:= substr('@nm_pessoa_fisica = ' || wheb_mensagem_pck.get_texto(301568) || ds_enter_w || -- Nome do funcionário
			   '@data_admissao = ' || wheb_mensagem_pck.get_texto(301569) || ds_enter_w || -- Data de admissão do funcionário
			   '@primeira_admissao = ' || wheb_mensagem_pck.get_texto(301570) || ds_enter_w || -- Data da primeira admissão
			   '@tempo_servico = ' || wheb_mensagem_pck.get_texto(301571),1,2000); -- Tempo de serviço do funcionário (anos)
			
elsif (ie_tipo_p = 'A') then
	ds_retorno_w	:= substr('@nm_pessoa_fisica = ' || wheb_mensagem_pck.get_texto(1099899),1,2000); ---- Nome da pessoa física
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
