CREATE OR REPLACE FUNCTION obter_macro_integracao_me (cd_tipo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000);
ds_enter_w		varchar(10) := chr(13) || chr(10);


BEGIN

if (cd_tipo_p = 1) then
ds_retorno_w := '@DOCUMENTO = '||wheb_mensagem_pck.get_texto(315198) || ds_enter_w ||--Documento (Solicitação compras, Pedido, Nota fiscal).
		'@NR_DOCUMENTO = '||wheb_mensagem_pck.get_texto(315199) || ds_enter_w ||--Número do documento.
		'@OPERACAO = '||wheb_mensagem_pck.get_texto(315200)|| ds_enter_w ||--Operação (Inclusão, Cancelamento, Alteração).
		'@TRANSICAO = '||wheb_mensagem_pck.get_texto(315201)|| ds_enter_w ||--Transição (se for do Tasy para ME ou do ME para Tasy).
		'@DS_ERRO = '||wheb_mensagem_pck.get_texto(315202);--Descrição do erro.
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
