CREATE OR REPLACE FUNCTION obter_macro_padrao_laudo_pat_atx ( nr_seq_laudo_p text, nr_linha_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_sql_w	varchar(250);
ds_retorno_w	varchar(4000);
nr_seq_texto_w	bigint;
BEGIN
ds_sql_w := 	'select a.ds_texto   			'||
		'from (              			'||
		'select a.ds_texto, rownum linha 	'||
		'from (					'||
		'select a.ds_texto,			'||
		'	c.ie_texto_grupo, 		'||
		'	d.nr_sequencia_princ		'||
		'from   grupo_questao_laudo c,		'||
		'	laudo_questao_item a,		'||
		'	laudo_grupo_questao b,		'||
		'	prescr_proc_peca d		'||
		'where  b.nr_seq_laudo    = :nr_seq_laudo_p '||
		'and    a.nr_seq_laudo_grupo  = b.nr_sequencia '||
		'and    c.nr_sequencia    = b.nr_seq_grupo_questao '||
		'and    d.nr_sequencia     = b.nr_seq_peca '||
		'and    nvl(d.ie_situacao,''A'') = ''A'' '||
		'order by  c.ie_texto_grupo, d.nr_sequencia_princ) a) a '||
		'where linha = :nr_linha_p ';

nr_seq_texto_w := converte_rtf_string(ds_sql_w, nr_seq_laudo_p||'#@#@'||nr_linha_p, nm_usuario_p, nr_seq_texto_w);

if (nr_seq_texto_w IS NOT NULL AND nr_seq_texto_w::text <> '') and (nr_seq_texto_w > 0) then

	select	ds_texto
	into STRICT	ds_retorno_w
	from	tasy_conversao_rtf
	where	nr_sequencia = nr_seq_texto_w;

end if;

commit;

return	ds_retorno_w;

end;
