			'@comprador = '||wheb_mensagem_pck.get_texto(320131) || ds_enter_w || --Comprador da ordem de compra.
			'@valor_ordem = '||wheb_mensagem_pck.get_texto(321605) || ds_enter_w ||--Valor da ordem de compra.
			'@fornecedor = '||wheb_mensagem_pck.get_texto(321606) || ds_enter_w || --Fornecedor da OC
			'@materiais = '||wheb_mensagem_pck.get_texto(318705)|| ds_enter_w ||--Lista de materiais solicitados.
			'@mat_complementar = '||wheb_mensagem_pck.get_texto(339610)|| ds_enter_w ||--Lista de materiais solicitados + descricao complementar
			'@mat_estrutura = '||wheb_mensagem_pck.get_texto(318706);--Lista de materiais + Estrutura do material.	
elsif	(cd_tipo_p in (85)) then
	ds_retorno_w := '@nota_fiscal = '||wheb_mensagem_pck.get_texto(320116) || ds_enter_w ||--Numero da nota fiscal.
			'@dt_nota = '||wheb_mensagem_pck.get_texto(321610)|| ds_enter_w ||--Data de emissao da nota fiscal.
			'@fornecedor = '||wheb_mensagem_pck.get_texto(321611);--Fornecedor da nota fiscal
elsif	(cd_tipo_p in (86)) then
	ds_retorno_w := '@nr_ordem_compra = '||wheb_mensagem_pck.get_texto(318682) || ds_enter_w ||--Numero da ordem de compra.
			'@nr_inspecao = '||wheb_mensagem_pck.get_texto(321617)|| ds_enter_w ||--Numero do registro da inspecao.
			'@mat_mtvo_dev = '||wheb_mensagem_pck.get_texto(321612) || ds_enter_w || --Lista dos materiais da inspecao com o motivo de devolucao.
			'@nao_conformidade = '|| wheb_mensagem_pck.get_texto(407945); -- Descricao da Nao conformidade.
elsif	(cd_tipo_p in (91)) then
	ds_retorno_w := '@Nr_requisicao = '||wheb_mensagem_pck.get_texto(321625)|| ds_enter_w ||--Nr. requisicao.
			'@Ds_consistencia = '||wheb_mensagem_pck.get_texto(321626);--Mensagem de consistencia de saldo.
elsif	(cd_tipo_p in (94)) then
	ds_retorno_w := '@agenda = '||wheb_mensagem_pck.get_texto(321620)|| ds_enter_w ||--Numero da agenda.
			'@data = '||wheb_mensagem_pck.get_texto(321619)|| ds_enter_w ||--Data.
			'@paciente = '||wheb_mensagem_pck.get_texto(321618)|| ds_enter_w ||--Nome do paciente.
			'@convenio = '||wheb_mensagem_pck.get_texto(321621)|| ds_enter_w ||--Convenio.
			'@procedimento = '||wheb_mensagem_pck.get_texto(321622)|| ds_enter_w ||--Procedimento.
			'@cirurgiao = '||wheb_mensagem_pck.get_texto(321623)|| ds_enter_w ||--Nome do cirurgiao.
			'@materiais = '||wheb_mensagem_pck.get_texto(321624);--Descricao dos materiais + Fornecedor + Quantidade, dos materiais transferidos'
elsif	(cd_tipo_p in (95)) then
	ds_retorno_w := '@cotacao = '||wheb_mensagem_pck.get_texto(318556);--Numero da cotacao de compra.
elsif	(cd_tipo_p in (96)) then
	ds_retorno_w := '@cdMaterial = '||wheb_mensagem_pck.get_texto(319913)|| ds_enter_w || --Codigo do material.'
			'@dsMaterial = '||wheb_mensagem_pck.get_texto(319918);  --Descricao do material.' ';
elsif (cd_tipo_p in (97)) then
	ds_retorno_w := '@nr_atendimento = '||wheb_mensagem_pck.get_texto(321629)|| ds_enter_w ||--Numero do atendimento.
			'@nr_seq_autorizacao = '||wheb_mensagem_pck.get_texto(321633)|| ds_enter_w ||--Numero da autorizacao.
			'@nm_paciente = '||wheb_mensagem_pck.get_texto(321618)|| ds_enter_w ||--Nome do paciente.
			'@cotacao = ' ||wheb_mensagem_pck.get_texto(318556) || ds_enter_w || --Numero da cotacao de compra.
			'@cnpj = '||wheb_mensagem_pck.get_texto(318639) || ds_enter_w ||--CNPJ do fornecedor.
			'@razao_pj = '||wheb_mensagem_pck.get_texto(318648) || ds_enter_w ||--Razao social da pessoa juridica.
			'@cdMaterial = '||wheb_mensagem_pck.get_texto(319913)|| ds_enter_w || --Codigo do material.' 
			'@dsMaterial = '||wheb_mensagem_pck.get_texto(319918); --Descricao do material.' 
elsif	(cd_tipo_p in (98,99)) then
	ds_retorno_w := '@cdCotacao = '||wheb_mensagem_pck.get_texto(318556) || ds_enter_w ||--Numero da cotacao de compra.
			'@dsMateriais = '||wheb_mensagem_pck.get_texto(321634);--Lista de materiais.
elsif	(cd_tipo_p in (100)) then
	ds_retorno_w :=	'@nr_solic_compra = '||wheb_mensagem_pck.get_texto(321635)|| ds_enter_w ||--Numero das solicitacoes que compoem a nota.
			'@nr_ordem_ordem = '||wheb_mensagem_pck.get_texto(318682) || ds_enter_w ||--Numero da ordem de compra.
			'@nm_solicitante = '||wheb_mensagem_pck.get_texto(318703) || ds_enter_w ||--Nome do solicitante da solicitacao.
			'@nr_solic_centro_custo = '||wheb_mensagem_pck.get_texto(321636)|| ds_enter_w ||--Numero da solicitacao + Descricao do centro de custo.
			'@nr_solic_local_estoque = '||wheb_mensagem_pck.get_texto(321637)|| ds_enter_w ||--Numero da solicitacao + Descricao do local de estoque.
			'@nr_seq_nota = '||wheb_mensagem_pck.get_texto(321029)|| ds_enter_w ||-- Sequencia da nota fiscal.
			'@nr_nota_fiscal = '||wheb_mensagem_pck.get_texto(320116) || ds_enter_w ||--Numero da nota fiscal.
			'@ds_material = '||wheb_mensagem_pck.get_texto(321638)|| ds_enter_w ||--Lista dos materiais entregues.
			'@ds_material_compl = '||wheb_mensagem_pck.get_texto(456777);
elsif	(cd_tipo_p in (101)) then
	ds_retorno_w :=	'@dt_geracao = '||wheb_mensagem_pck.get_texto(321639)|| ds_enter_w ||--Data de geracao do log de erro.
			'@ds_erro = '||wheb_mensagem_pck.get_texto(321645);--Erro retornado pelo Webservice.
elsif	(cd_tipo_p in (103)) then
	ds_retorno_w :=	'@cnpj = '||wheb_mensagem_pck.get_texto(318639) || ds_enter_w || --CNPJ do fornecedor.
			'@razao_pj = '||wheb_mensagem_pck.get_texto(318648) || ds_enter_w ||--Razao social da pessoa juridica.
			'@nr_nota_fiscal = '||wheb_mensagem_pck.get_texto(320116) || ds_enter_w ||--Numero da nota fiscal.
			'@lista_itens = '||wheb_mensagem_pck.get_texto(321646);--Mostra a nao conformidade e os itens com essa nao conformidade. Deve ser utilizado em uma nova linha para mostrar em forma de lista.
elsif	(cd_tipo_p in (104)) then
	ds_retorno_w :=	'@cnpj = '||wheb_mensagem_pck.get_texto(318639) || ds_enter_w ||--CNPJ do fornecedor.
			'@razao_pj = '||wheb_mensagem_pck.get_texto(318648) || ds_enter_w ||--Razao social da pessoa juridica.
			'@nr_nota_fiscal = '||wheb_mensagem_pck.get_texto(320116) || ds_enter_w ||--Numero da nota fiscal.
			'@lista_itens = '||wheb_mensagem_pck.get_texto(321647);--Mostra o motivo de devolucao e os itens com esse motivo. Deve ser utilizado em uma nova linha para mostrar em forma de lista.
elsif	(cd_tipo_p in (105)) then
	ds_retorno_w :=	'@nr_sequencia = '||wheb_mensagem_pck.get_texto(321649) || ds_enter_w ||--Numero da solicitacao/emprestimo/transferencia.
			'@nr_registro = '||wheb_mensagem_pck.get_texto(321650) || ds_enter_w ||--Numero da lista de analise PCS.
			'@ie_tipo_registro = '||wheb_mensagem_pck.get_texto(321651) || ds_enter_w ||--Identifica o tipo do registro criado solicitacao/emprestimo/transferencia.
			'@ds_lista_itens = '||wheb_mensagem_pck.get_texto(321652);--Exibe os itens pertecentes ao registro criado.			
elsif	(cd_tipo_p in (106)) then
    select max(ds_macro)
    into ds_macro_w
    from funcao_macro 
    where cd_funcao = 982 
    and nr_sequencia = 5734;
    
    ds_retorno_w :=	'@nr_seq_parecer = '||wheb_mensagem_pck.get_texto(321653) || ds_enter_w ||--Numero do parecer.
			ds_macro_w || ' = '||wheb_mensagem_pck.get_texto(321654)|| ds_enter_w ||--Destinatario do parecer
			'@ds_material = '||wheb_mensagem_pck.get_texto(319918)|| ds_enter_w ||  --Descricao do material.'
			'@nr_registro = '||wheb_mensagem_pck.get_texto(321650) || ds_enter_w ||--Numero da lista de analise PCS.
			'@ds_origem = '||wheb_mensagem_pck.get_texto(321655)|| ds_enter_w ||--Origem do parecer (Reclassificacao/Lista)
			'@ds_resposta = '||wheb_mensagem_pck.get_texto(321656);--Resposta do parecer
elsif (cd_tipo_p in (108)) then
	ds_retorno_w := '@cotacao = '||wheb_mensagem_pck.get_texto(318556) || ds_enter_w ||--Numero da cotacao de compra.
			'@ds_lista_materiais = '||wheb_mensagem_pck.get_texto(321634);--Lista de materiais.			
elsif (cd_tipo_p in (109)) then
	ds_retorno_w :=	'@cnpj = '||wheb_mensagem_pck.get_texto(318639) || ds_enter_w || --CNPJ do fornecedor.
			'@razao_pj = '||wheb_mensagem_pck.get_texto(318648) || ds_enter_w ||--Razao social da pessoa juridica.
			'@nr_nota_fiscal = '||wheb_mensagem_pck.get_texto(320116) || ds_enter_w ||--Numero da nota fiscal.
			'@lista_itens = '||wheb_mensagem_pck.get_texto(328394);--Mostra a lista com os itens e as divergencias.
end if;

if (cd_tipo_p not in (94,97,95,96,98,99,100,101,103,104,105,106,108,109)) then
	ds_retorno_w	:=	ds_retorno_w || ds_enter_w ||
			wheb_mensagem_pck.get_texto(321657)|| ds_enter_w ||--Para quem utiliza a funcao Gestao de Envio de E-mail do compras, e estiver parametrizado para enviar e-mail agrupado, pode utilizar o simbolo % para agrupar somente parte da mensagem.
			wheb_mensagem_pck.get_texto(321658)|| ds_enter_w ||--Exemplo: As solicitacoes abaixo foram aprovadas.
			wheb_mensagem_pck.get_texto(321659)|| ds_enter_w ||--%Solicitacao: @solicitacao
			wheb_mensagem_pck.get_texto(321663);--'Solicitante: @solicitante%';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_macro_tipo_msg_compra ( cd_tipo_p bigint) FROM PUBLIC;

