CREATE OR REPLACE FUNCTION obter_mail_pf_concat_mmed ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_email_ww			varchar(2000);
ds_email_w			varchar(255);

C01 CURSOR FOR
SELECT	ds_email
FROM	compl_pessoa_fisica
WHERE	cd_pessoa_fisica = cd_pessoa_fisica_p
and 	(ds_email IS NOT NULL AND ds_email::text <> '')
order by 1;


BEGIN

open C01;
loop
fetch C01 into
	ds_email_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_email_ww := ds_email_ww || ds_email_w || ';';

	end;
end loop;
close C01;

ds_email_ww := substr(ds_email_ww,1,length(ds_email_ww) - 1);

return ds_email_ww;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
