CREATE OR REPLACE FUNCTION obter_maior_preco_brasindice ( cd_estabelecimento_p bigint, cd_material_p bigint, dt_vigencia_p timestamp, cd_unidade_medida_param_p text, ie_tipo_preco_p text) RETURNS bigint AS $body$
DECLARE



vl_brasindice_w			double precision	:= 0;
cd_unidade_medida_compra_w	varchar(30);
cd_unidade_medida_estoque_w	varchar(30);
cd_unidade_medida_consumo_w	varchar(30);
qt_conv_compra_estoque_w		double precision;
qt_conv_estoque_consumo_w	double precision;
qt_existe_w			bigint;

c01 CURSOR FOR
	SELECT	coalesce(max(dividir(b.vl_preco_medicamento, coalesce(a.qt_conversao,1))),0)
	from	material_brasindice a,
		brasindice_preco b
	where	a.cd_material		= cd_material_p
	and	a.cd_laboratorio		= b.cd_laboratorio
	and	a.cd_medicamento		= b.cd_medicamento
	and	a.cd_apresentacao		= b.cd_apresentacao
	and	coalesce(a.ie_situacao, 'A')	= 'A'
	and	coalesce(a.cd_estabelecimento, coalesce(cd_estabelecimento_p, 0))	= coalesce(cd_estabelecimento_p, 0)
	and	b.ie_tipo_preco			= ie_tipo_preco_p
	and	coalesce(b.dt_inicio_vigencia,dt_vigencia_p) <= dt_vigencia_p
	order by b.dt_inicio_vigencia;


BEGIN

/* Adicionei este count pois em alguns clientes, se o material nÃ£o existe na tabela
a consulta demorava mais de 4 minutos */
select	count(*)
into STRICT	qt_existe_w
from	material_brasindice a,
	brasindice_preco b
where	a.cd_material		= cd_material_p
and	a.cd_laboratorio		= b.cd_laboratorio
and	a.cd_medicamento		= b.cd_medicamento
and	a.cd_apresentacao		= b.cd_apresentacao
and	coalesce(a.ie_situacao, 'A')	= 'A'
and	coalesce(a.cd_estabelecimento, coalesce(cd_estabelecimento_p, 0))	= coalesce(cd_estabelecimento_p, 0)
and	b.ie_tipo_preco			= ie_tipo_preco_p;

if (qt_existe_w > 0) then
	begin

	open C01;
	loop
	fetch C01 into
		vl_brasindice_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		null;
		end;
	end loop;
	close C01;

	if (vl_brasindice_w > 0) then
		begin

		select	cd_unidade_medida_compra,
			cd_unidade_medida_estoque,
			cd_unidade_medida_consumo,
			qt_conv_compra_estoque,
			qt_conv_estoque_consumo
		into STRICT	cd_unidade_medida_compra_w,
			cd_unidade_medida_estoque_w,
			cd_unidade_medida_consumo_w,
			qt_conv_compra_estoque_w,
			qt_conv_estoque_consumo_w
		from	material
		where	cd_material = cd_material_p;

		if (cd_unidade_medida_param_p = cd_unidade_medida_compra_w) then
			vl_brasindice_w	:= vl_brasindice_w * qt_conv_compra_estoque_w * qt_conv_estoque_consumo_w;
		elsif (cd_unidade_medida_param_p = cd_unidade_medida_estoque_w) then
			vl_brasindice_w	:= vl_brasindice_w * qt_conv_estoque_consumo_w;
		elsif (cd_unidade_medida_param_p = cd_unidade_medida_consumo_w) then
			vl_brasindice_w	:= vl_brasindice_w * qt_conv_estoque_consumo_w;
		end if;

		end;
	end if;

	end;
end if;

return	vl_brasindice_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
