SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_maior_tempo_espera_fila (nr_seq_fila_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp) RETURNS varchar AS $body$
DECLARE

			
ds_maior_tempo_espera_w	bigint;
ie_forma_tempo_espera_w	varchar(1);
dt_inicio_w		timestamp;
qt_maior_total_seg_w		bigint;
nr_horas_w		bigint;
ds_tempo_w		varchar(11);
qt_senhas_w		bigint;
nr_sequencia_w		bigint;
nr_seq_movimenacao_w	bigint;
qt_maior_total_min_w	bigint;
qt_segundo_w 		bigint;
ds_tempo_espera_w	varchar(20);
dt_entrada_w		timestamp;
dt_saida_w		timestamp;
dt_inicio_atendimento_w	timestamp;

C01 CURSOR FOR
	SELECT	a.nr_seq_pac_senha_fila,
		a.nr_sequencia,
		a.dt_entrada,
		a.dt_saida
	from	movimentacao_senha_fila a,
		paciente_senha_fila b
	where	a.nr_seq_fila_espera = nr_seq_fila_p
	and	trunc(a.dt_entrada) between trunc(dt_inicio_p) and (dt_fim_p)
	and	b.nr_sequencia = a.nr_seq_pac_senha_fila
	and	coalesce(b.dt_inutilizacao::text, '') = ''
	and	((b.nr_seq_fila_senha = a.nr_seq_fila_espera) or (exists (	SELECT	1
										from	atendimentos_senha c
										where	c.nr_seq_pac_senha_fila = a.nr_seq_pac_senha_fila
										and	c.nr_seq_fila_espera = a.nr_seq_fila_espera)));



BEGIN
qt_maior_total_seg_w := 0;
nr_horas_w   := 0;
qt_senhas_w  := 0;
qt_maior_total_min_w := 0;
qt_segundo_w := 0;

ie_forma_tempo_espera_w := Obter_param_Usuario(10021, 77, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_forma_tempo_espera_w);

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	nr_seq_movimenacao_w,
	dt_entrada_w,
	dt_saida_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	
	If (ie_forma_tempo_espera_w = 'F') then
		select	max(dt_entrada)
		into STRICT	dt_inicio_w
		from	movimentacao_senha_fila
		where	nr_sequencia = nr_seq_movimenacao_w;

		If (coalesce(dt_inicio_w::text, '') = '') then
			select	max(coalesce(dt_entrada_fila, dt_geracao_senha))
			into STRICT	dt_inicio_w
			from	paciente_senha_fila a
			where	a.nr_sequencia = nr_sequencia_w;
		end if;
	else
		select	max(dt_geracao_senha)
		into STRICT	dt_inicio_w
		from	paciente_senha_fila
		where	nr_sequencia = nr_sequencia_w;
	end if;

	begin
		select	max(a.dt_inicio_atendimento)
		into STRICT	dt_inicio_atendimento_w
		from	atendimentos_senha a
		where	nr_seq_pac_senha_fila = nr_sequencia_w
		and	a.dt_inicio_atendimento between dt_entrada_w and coalesce(dt_saida_w, clock_timestamp())
		and	a.nr_seq_fila_espera = nr_seq_fila_p;
		
		if (coalesce(dt_inicio_atendimento_w::text, '') = '') then
			select	max(dt_primeira_chamada)
			into STRICT	dt_inicio_atendimento_w
			from	paciente_senha_fila
			where	nr_sequencia = nr_sequencia_w;
		end if;
	exception
		when	others then
			dt_inicio_atendimento_w := null;
	end;
	
	qt_senhas_w := qt_senhas_w + 1;
		begin
			qt_segundo_w := round((coalesce(dt_inicio_atendimento_w, clock_timestamp()) - dt_inicio_w) * 86400);
		exception
		when others then
			qt_segundo_w := 9999999999;
		end;
		
	If (greatest(qt_segundo_w, 0) > qt_maior_total_seg_w) then
		qt_maior_total_seg_w := qt_segundo_w;
	end if;
	
	
end loop;
close C01;

if (coalesce(qt_senhas_w,0) > 0) then
	
	while(qt_maior_total_seg_w > 60) loop
		qt_maior_total_min_w := qt_maior_total_min_w + 1;
		qt_maior_total_seg_w := qt_maior_total_seg_w - 60;
	end loop;
	
	while(qt_maior_total_min_w > 60) loop
		nr_horas_w := nr_horas_w + 1;
		qt_maior_total_min_w := qt_maior_total_min_w - 60;
	end loop;
	
	if (nr_horas_w < 100) then
		ds_tempo_w := lpad(nr_horas_w, 2, '0') || ':' || lpad(qt_maior_total_min_w, 2, '0') || ':' || lpad(coalesce(qt_maior_total_seg_w,'00'), 2, '0');
		
	elsif (nr_horas_w < 1000) then
		ds_tempo_w := lpad(nr_horas_w, 3, '0') || ':' || lpad(qt_maior_total_min_w, 2, '0') || ':' || lpad(coalesce(qt_maior_total_seg_w,'00'), 2, '0');
		
	elsif (nr_horas_w < 10000) then
		ds_tempo_w := lpad(nr_horas_w, 4, '0') || ':' || lpad(qt_maior_total_min_w, 2, '0') || ':' || lpad(coalesce(qt_maior_total_seg_w,'00'), 2, '0');
		
	else
		ds_tempo_w := lpad(nr_horas_w, 5, '0') || ':' || lpad(qt_maior_total_min_w, 2, '0') || ':' || lpad(coalesce(qt_maior_total_seg_w,'00'), 2, '0');
	end if;

	ds_tempo_espera_w := ds_tempo_w;

end if;

If (coalesce(ds_tempo_espera_w::text, '') = '') then
	ds_tempo_espera_w := '00:00:00';
end if;

return	ds_tempo_espera_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_maior_tempo_espera_fila (nr_seq_fila_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp) FROM PUBLIC;

