CREATE OR REPLACE FUNCTION obter_maior_valor_compra_geral ( cd_material_p bigint, cd_local_estoque_p bigint, ie_tipo_p text, cd_unidade_medida_p text) RETURNS bigint AS $body$
DECLARE


/*Function correta para buscar o maior valor de compra de um material.*/

vl_maior_valor_w			double precision;
vl_select_w			double precision;
nr_sequencia_w			bigint;
cd_material_estoque_w		integer;
cd_local_estoque_w		bigint;
nr_item_nf_w			bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.nr_item_nf
from	natureza_operacao o,
	operacao_nota p,
	nota_fiscal b,
	nota_fiscal_item a
where	a.nr_sequencia			= b.nr_sequencia
and	b.cd_natureza_operacao		= o.cd_natureza_operacao
and	b.cd_operacao_nf 		= p.cd_operacao_nf
and	a.dt_atualizacao		>= clock_timestamp() - interval '365 days'
and	((ie_tipo_p = 'N' AND a.cd_material = cd_material_p) or
	 (ie_tipo_p = 'S' AND a.cd_material_estoque = cd_material_p))
and	b.ie_situacao			= '1'
and	o.ie_entrada_saida		= 'E'
and	p.ie_ultima_compra		= 'S'
and	obter_tipo_operacao_nota(b.cd_operacao_nf) <> 'T'
and	a.cd_unidade_medida_compra	= cd_unidade_medida_p
and	((cd_local_estoque_w = 0) or
	(cd_local_estoque_w > 0 AND a.cd_local_estoque = cd_local_estoque_w));


BEGIN

cd_local_estoque_w	:= coalesce(cd_local_estoque_p,0);
vl_maior_valor_w	:= 0;
vl_select_w		:= 0;

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	nr_item_nf_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (coalesce(ie_tipo_p, 'N') = 'N') then

		select	coalesce(max(vl_unitario_item_nf),0)
		into STRICT	vl_select_w
		from 	nota_fiscal_item a
		where	nr_sequencia 			= nr_sequencia_w
		and	nr_item_nf			= nr_item_nf_w
		and	cd_material			= cd_material_p
		and	a.cd_unidade_medida_compra	= cd_unidade_medida_p;

		if (vl_select_w > vl_maior_valor_w) then
			vl_maior_valor_w	:= vl_select_w;
		end if;

	elsif (coalesce(ie_tipo_p, 'N') <> 'N') then

		select	coalesce(max(vl_unitario_item_nf),0)
		into STRICT	vl_select_w
		from 	nota_fiscal_item a
		where	nr_sequencia 			= nr_sequencia_w
		and	nr_item_nf			= nr_item_nf_w
		and	cd_material_estoque		= cd_material_p
		and	a.cd_unidade_medida_compra	= cd_unidade_medida_p;

		if (vl_select_w > vl_maior_valor_w) then
			vl_maior_valor_w	:= vl_select_w;
		end if;
	end if;
	end;
end loop;
close C01;


return vl_maior_valor_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
