CREATE OR REPLACE FUNCTION obter_man_exec_estagio (nr_ordem_servico_p bigint, nr_seq_estagio_p bigint) RETURNS varchar AS $body$
DECLARE


nm_usuario_exec_w	varchar(15);
nr_seq_exec_w		bigint;
nm_usuario_w		varchar(80);
ds_resultado_w		varchar(80);
cd_perfil_ativo_w		integer := obter_perfil_ativo;


BEGIN

if (NR_ORDEM_SERVICO_P IS NOT NULL AND NR_ORDEM_SERVICO_P::text <> '') then
	begin
	select coalesce(max(nm_usuario_exec), 'NÃ£o Informado')
	into STRICT	nm_usuario_exec_w
	from	man_ordem_servico
	where	nr_sequencia 	= nr_ordem_servico_p;


	select coalesce(max(a.nr_sequencia),0)
	into STRICT	nr_seq_exec_w
	from	man_estagio_usuario c,
		man_ordem_servico_exec a,
		man_ordem_servico b
	where	a.nr_seq_ordem = b.nr_sequencia
	and	a.nm_usuario_exec = c.nm_usuario_acao
	and	a.nr_seq_ordem 	= nr_ordem_servico_p
	and  	c.nr_seq_estagio = nr_seq_estagio_p
	and	CASE WHEN coalesce(c.cd_perfil,0)=0 THEN coalesce(cd_perfil_ativo_w,0)  ELSE c.cd_perfil END  =
		CASE WHEN coalesce(cd_perfil_ativo_w,0)=0 THEN CASE WHEN coalesce(c.cd_perfil,0)=0 THEN coalesce(cd_perfil_ativo_w,0)  ELSE c.cd_perfil END   ELSE cd_perfil_ativo_w END;


	if (nr_seq_exec_w <> 0) then
		begin
		select nm_usuario_exec
		into STRICT	nm_usuario_w
		from	man_ordem_servico_exec
		where	nr_seq_ordem 	= nr_ordem_servico_p
		and	nr_sequencia	= nr_seq_exec_w;

		ds_resultado_w	:= nm_usuario_w;
		end;
	end if;
	end;
end if;

RETURN DS_RESULTADO_W;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
