CREATE OR REPLACE FUNCTION obter_maquina_equipamento ( nr_prescricao_p bigint, nr_seq_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


sg_maquina_w	lab_equip_maquina.sg_maquina%type;
nr_seq_exame_w	exame_laboratorio.nr_seq_exame%type;


BEGIN

select	max(a.nr_seq_exame)
into STRICT	nr_seq_exame_w
from	prescr_procedimento a
where	a.nr_prescricao = nr_prescricao_p
and	a.nr_sequencia = nr_seq_prescricao_p;

if (nr_seq_exame_w IS NOT NULL AND nr_seq_exame_w::text <> '') then

	select	max(sg_maquina)
	into STRICT	sg_maquina_w
	from (
		SELECT	b.sg_maquina,
			b.nr_sequencia,
			count(*) qt
		from	lab_equip_maquina_exame a,
			lab_equip_maquina b
		where	a.nr_seq_maquina = b.nr_sequencia
		and	coalesce(b.ie_situacao, 'A') = 'A'
		and	a.nr_seq_exame in (	SELECT	c.nr_seq_exame
						from	prescr_procedimento c
						where	c.nr_prescricao = nr_prescricao_p)
		and	exists (	select	1
				from	lab_equip_maquina_exame c
				where	c.nr_seq_maquina = a.nr_seq_maquina
				and	c.nr_seq_exame = nr_seq_exame_w)
		group by b.sg_maquina, b.nr_sequencia
		order by qt desc, b.nr_sequencia) alias6 LIMIT 1;

end if;

return	sg_maquina_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
