CREATE OR REPLACE FUNCTION obter_marcas_aceitaveis_solic ( nr_cot_compra_p bigint, nr_item_cot_compra_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000);
nr_solic_compra_w		bigint;
nr_item_solic_compra_w	bigint;


BEGIN

select	coalesce(max(nr_solic_compra),0),
	coalesce(max(nr_item_solic_compra),0)
into STRICT	nr_solic_compra_w,
	nr_item_solic_compra_w
from	cot_compra_item
where	nr_cot_compra = nr_cot_compra_p
and	nr_item_cot_compra = nr_item_cot_compra_p;

select	ds_marcas_aceitaveis
into STRICT	ds_retorno_w
from	solic_compra_item
where	nr_solic_compra = nr_solic_compra_w
and	nr_item_solic_compra = nr_item_solic_compra_w;

return	ds_retorno_w;

end;
$body$
