CREATE OR REPLACE FUNCTION obter_marca_item_cotacao ( nr_seq_cot_item_forn_p bigint) RETURNS varchar AS $body$
DECLARE


ds_marca_w		varchar(30);


BEGIN

select	substr(coalesce(ds_marca_fornec, ds_marca),1,30)
into STRICT	ds_marca_w
from	cot_compra_forn_item
where	nr_sequencia = nr_seq_cot_item_forn_p;

return	ds_marca_w;

end;
$body$
