CREATE OR REPLACE FUNCTION obter_marca_material_opme ( cd_material_p bigint, nr_seq_marca_p bigint) RETURNS varchar AS $body$
DECLARE


ds_marca_w		varchar(80);


BEGIN

select	max(substr(b.ds_marca,1,80))
into STRICT	ds_marca_w
from	marca b,
	material_marca a
where	a.cd_material		= cd_material_p
and	a.nr_sequencia		= nr_seq_marca_p
and	a.nr_sequencia		= b.nr_sequencia;

return	ds_marca_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
