CREATE OR REPLACE FUNCTION obter_margem ( vl_custo_p bigint, vl_venda_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_margem_w		double precision;
pr_margem_w		double precision;


BEGIN

vl_margem_w		:= coalesce(vl_venda_p,0) - coalesce(vl_custo_p,0);
pr_margem_w		:= dividir(vl_margem_w, vl_custo_p) * 100;
if (coalesce(vl_venda_p,0) = 0) then
	pr_margem_w	:= 0;
end if;
if (ie_opcao_p = 'P') then
	return	pr_margem_w;
else
	return	vl_margem_w;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
