CREATE OR REPLACE FUNCTION obter_mascara_cnpj_fin ( cd_cgc_p text) RETURNS varchar AS $body$
DECLARE


/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++

1399597 - Utilizada na consulta de títulos a receber.

Foi duplicada da function existente obter_cnpj_mascara pois nessa foi tratada exceção, pois
existem casos onde o CNPJ foi cadastrado com caracteres diferente de números.

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w	varchar(255);


BEGIN

begin
select	CASE WHEN coalesce(cd_cgc_p::text, '') = '' THEN null  ELSE replace(replace(replace(to_char(lpad(replace(cd_cgc_p, ' ', ''),14,'0'),'00,000,000,0000,00'),',','.'), ' ', '')		,'.'||trim(both to_char(trunc(mod(lpad(cd_cgc_p,14,'0'),1000000)/100),'0000'))||'.'		,'/'||trim(both to_char(trunc(mod(lpad(cd_cgc_p,14,'0'),1000000)/100),'0000'))||'-') END
into STRICT	ds_retorno_w
;
exception when others then
	ds_retorno_w := '';
end;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
