CREATE OR REPLACE FUNCTION obter_mascara_guia_conv ( cd_estabelecimento_p bigint, cd_convenio_p bigint, ie_tipo_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_mascara_w		varchar(30);


BEGIN 
 
select	max(ds_mascara_guia) 
into STRICT	ds_mascara_w 
from	convenio_estabelecimento 
where	cd_estabelecimento = cd_estabelecimento_p 
and	cd_convenio = cd_convenio_p;
 
if (coalesce(ds_mascara_w::text, '') = '') then 
 
	select	max(a.ds_mascara_guia) 
	into STRICT	ds_mascara_w 
	from	convenio a 
	where	a.cd_convenio	= cd_convenio_p;
	 
end if;
 
return	ds_mascara_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
