CREATE OR REPLACE FUNCTION obter_mascara_guia_item ( cd_estabelecimento_p bigint, cd_convenio_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_mascara_w		varchar(30);


BEGIN 
 
if (ie_opcao_p = 'P') then 
	select	max(a.ds_mascara_guia_proc) 
	into STRICT	ds_mascara_w 
	from	convenio_estabelecimento a 
	where	a.cd_convenio		= cd_convenio_p 
	and		a.cd_estabelecimento	= cd_estabelecimento_p;
end if;
 
return	ds_mascara_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
