CREATE OR REPLACE FUNCTION obter_mascara_procedimento ( cd_procedimento_p text) RETURNS varchar AS $body$
DECLARE


qt_tamanho_w		bigint;
cd_procedimento_w	varchar(15);


BEGIN

select 	length(cd_procedimento_p)
into STRICT	qt_tamanho_w
;

if (qt_tamanho_w = 8) then
	cd_procedimento_w:=  substr(cd_procedimento_p,1,2) || '.' || substr(cd_procedimento_p,3,2) || '.' || substr(cd_procedimento_p,5,3) || '-' || substr(cd_procedimento_p,8,1);
else
	cd_procedimento_w:=  cd_procedimento_p;
end if;

return cd_procedimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
