CREATE OR REPLACE FUNCTION obter_mascara_senha ( cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_categoria_p text, ie_tipo_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_mascara_w		varchar(30);
ie_mascara_w		varchar(5)	:=	'S';
ds_mascara_ww		varchar(30);


BEGIN

select	max(a.ds_mascara_codigo)
into STRICT	ds_mascara_ww
from	conv_mascara_senha a
where	a.cd_convenio					= cd_convenio_p
and	coalesce(a.ie_tipo_atendimento, ie_tipo_atendimento_p)	= ie_tipo_atendimento_p
and	((coalesce(cd_categoria_p::text, '') = '') or (coalesce(a.cd_categoria, cd_categoria_p)	= cd_categoria_p))
and	a.cd_estabelecimento	= cd_estabelecimento_p;


return	ds_mascara_ww;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
