CREATE OR REPLACE FUNCTION obter_materiais_mat_diluicao ( cd_diluente_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(4000);
cd_material_w	integer;
ds_material_w	varchar(255);

C01 CURSOR FOR
SELECT	cd_material,
	substr(obter_desc_material(cd_material),1,40) ds_material
from	material_diluicao
where	cd_diluente = cd_diluente_p;

/*
ie_opcao_p
'C' - Código
'D' - Descrição
*/
BEGIN
ds_retorno_w := '';

open C01;
loop
fetch C01 into
	cd_material_w,
	ds_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_opcao_p = 'C') then
		ds_retorno_w := substr(ds_retorno_w || cd_material_w || ', ',1,4000);
	elsif (ie_opcao_p = 'D') then
		ds_retorno_w := substr(ds_retorno_w || ds_material_w || ', ',1,4000);
	end if;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
