CREATE OR REPLACE FUNCTION obter_material_atend_pac (nr_prescricao_p bigint, nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_material_w	varchar(100);


BEGIN

begin
select	substr(obter_desc_material(cd_material),1,100)
into STRICT	ds_material_w
from 	material_atend_paciente
where 	nr_sequencia_prescricao	= nr_seq_material_p
and	nr_prescricao		= nr_prescricao_p;
exception
	when others then
	ds_material_w	:= '';
end;

return ds_material_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
