CREATE OR REPLACE FUNCTION obter_material_brasindice ( cd_medicamento_p text, cd_apresentacao_p text, cd_laboratorio_p text) RETURNS bigint AS $body$
DECLARE

cd_material_w		integer;

C01 CURSOR FOR
	SELECT	cd_material
	from	material_brasindice
	where	cd_medicamento		= cd_medicamento_p
	and	cd_medicamento		= coalesce(cd_medicamento_p,cd_medicamento)
	and	cd_laboratorio		= coalesce(cd_laboratorio_p,cd_laboratorio)
	and	coalesce(IE_SITUACAO, 'A')	= 'A'
	and	coalesce(dt_vigencia,clock_timestamp())<= clock_timestamp()
	order by coalesce(dt_vigencia,clock_timestamp() - interval '1000 days'),
		nr_sequencia;


BEGIN


open C01;
loop
fetch C01 into
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

return	cd_material_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
