CREATE OR REPLACE FUNCTION obter_material_conta ( cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_categoria_p text, cd_material_prescr_p bigint, cd_material_exec_p bigint, cd_material_p bigint, cd_plano_conv_p text, cd_setor_atend_p bigint, dt_vigencia_p timestamp, cd_local_estoque_p bigint, nr_prescricao_p bigint) RETURNS bigint AS $body$
DECLARE

/*
   ie_regra para obtenção do material para conta
	p - prescrição
	i - informado(código de barras ou digitado)
	c - comercial
	o - material da conta do cadastro de materiais (item prescrito)
	g - generico se tiver
	a - material da conta do cadastro de materiais (item atendido)

caso exista um registro adequado ao material na tabela
material_convenio o sistema utiliza este
*/
ie_regra_w			varchar(3);
cd_material_w			integer;
i				integer;
ie_regra_ww			varchar(1);
dt_vigencia_w			timestamp;
ie_tipo_material_w			varchar(3);

c01 CURSOR FOR
	SELECT 	cd_material_conta
	from	material_convenio
	where	cd_convenio = cd_convenio_p
	and	coalesce(cd_categoria, coalesce(cd_categoria_p,'0')) = coalesce(cd_categoria_p,'0')
	and	cd_material = cd_material_p
	and	coalesce(cd_setor_atendimento, cd_setor_atend_p) = cd_setor_atend_p
	and	dt_vigencia_w between coalesce(dt_vigencia_inicial, dt_vigencia_w) and coalesce(dt_vigencia_final, dt_vigencia_w)
	and	coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_p,0)) = coalesce(cd_estabelecimento_p,0)
	order by	coalesce(cd_setor_atendimento, 0),
		coalesce(dt_vigencia_inicial, to_date('01/01/1900','dd/mm/yyyy')),
		coalesce(cd_estabelecimento,0);


BEGIN

dt_vigencia_w:= coalesce(dt_vigencia_p, clock_timestamp());

open C01;
loop
fetch C01 into
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	cd_material_w	:= cd_material_w;
end loop;
close c01;

/* Retirado por Fabio em 31/07/2010 - Criado o cursor acima, para atender a distinção de setores para um mesmo convênio
select 	max(cd_material_conta)
into	cd_material_w
from	material_convenio
where	cd_convenio		= cd_convenio_p
and	cd_material		= cd_material_p
and	nvl(cd_setor_atendimento, cd_setor_atend_p) = cd_setor_atend_p
order by nvl(cd_setor_atendimento, 0);*/
select	max(ie_tipo_material)
into STRICT	ie_tipo_material_w
from	material
where	cd_material = cd_material_p;


select	obter_regra_material_conta(cd_convenio_p, cd_categoria_p,cd_estabelecimento_p, 'EP', cd_plano_conv_p, cd_setor_atend_p, cd_local_estoque_p, ie_tipo_material_w, cd_material_p)
into STRICT	ie_regra_w
;

for i in 1 .. 3 loop
	if (coalesce(cd_material_w::text, '') = '') and (ie_regra_w IS NOT NULL AND ie_regra_w::text <> '') then
		begin
		ie_regra_ww	:= substr(ie_regra_w,i,1);
		if (ie_regra_ww = 'P') then
			cd_material_w	:= cd_material_prescr_p;
		elsif (ie_regra_ww = 'I') then
			cd_material_w	:= cd_material_exec_p;
		elsif (ie_regra_ww = 'C') then
			select obter_mat_comercial(cd_material_p, 'COD')
			into STRICT cd_material_w
			;
		elsif (ie_regra_ww = 'G') then
			select cd_material_generico
			into STRICT cd_material_w
			from material
			where	cd_material	= cd_material_p;
		elsif (ie_regra_ww = 'O') then
			select	obter_mat_estabelecimento(cd_estabelecimento_p, 0, cd_material_p,'MC')
			into STRICT	cd_material_w
			;
		elsif (ie_regra_ww = 'A') then
			select	obter_mat_estabelecimento(cd_estabelecimento_p, 0, cd_material_exec_p,'MC')
			into STRICT	cd_material_w
			;
		elsif (ie_regra_ww = 'M') then /*matheus os 77447 09/01/2008*/
			select coalesce(max(cd_material_conta),cd_material_prescr_p)
			into STRICT	cd_material_w
			from	material_convenio
			where	cd_convenio	= cd_convenio_p
			and	cd_material	= cd_material_prescr_p;
		elsif (ie_regra_ww = 'B') then
			if (coalesce(nr_prescricao_p,0) > 0) then
				cd_material_w	:= cd_material_prescr_p;
			end if;
		end if;
		end;
	end if;
end loop;
if (coalesce(cd_material_w::text, '') = '') then
	cd_material_w	:= cd_material_p;
end if;

return cd_material_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
