CREATE OR REPLACE FUNCTION obter_material_cot_compra ( nr_cot_compra_p bigint, nr_item_cot_compra_p bigint) RETURNS varchar AS $body$
DECLARE


ds_resultado_w	varchar(100);


BEGIN

select	substr(b.ds_material,1,100)
into STRICT	ds_resultado_w
from	material b,
	cot_compra_item a
where a.cd_material = b.cd_material
  and a.nr_cot_compra = nr_cot_compra_p
  and a.nr_item_cot_compra = nr_item_cot_compra_p;

Return ds_resultado_w;

END;
$body$
LANGUAGE PLPGSQL
