CREATE OR REPLACE FUNCTION obter_material_custo_benef ( cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


cd_material_w		integer;
ds_material_w		varchar(101);
nr_seq_prioridade_w	bigint;
ds_retorno_w		varchar(2000);
ie_custo_benef_w		char(1);

C01 CURSOR FOR

	SELECT	cd_material_beneficio,
		substr(obter_desc_material(cd_material_beneficio),1,100),
		nr_seq_prioridade
	from	material_opcao_beneficio
	where	cd_material = cd_material_p
	order by	nr_seq_prioridade;


BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	open C01;
	loop
	fetch C01 into
		cd_material_w,
		ds_material_w,
		nr_seq_prioridade_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

			if (cd_material_w IS NOT NULL AND cd_material_w::text <> '') then

				ds_retorno_w := ds_retorno_w || wheb_mensagem_pck.get_texto(798501) || ' :  ' || cd_material_w || ' - ' || ds_material_w || chr(13) || chr(10);

			end if;
		end;
	end loop;
	close C01;


end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
