CREATE OR REPLACE FUNCTION obter_material_exame_lab (nr_seq_material_p bigint, cd_material_exame_p text, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE

nr_sequencia_w             bigint;
cd_material_exame_w        varchar(20);
ds_material_exame_w        varchar(50);
ds_retorno_w       varchar(100);
/* ie_opcao_p
   1 - nr_sequencia
   2 - cd_material_exame
   3 - ds_material_exame
*/
BEGIN
ds_retorno_w := '';
if (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') or (cd_material_exame_p IS NOT NULL AND cd_material_exame_p::text <> '') then
	select	nr_sequencia,
		cd_material_exame,
		ds_material_exame
	into STRICT	nr_sequencia_w,
		cd_material_exame_w,
		ds_material_exame_w
	from material_exame_lab
	where nr_sequencia = coalesce(nr_seq_material_p,nr_sequencia)
	  and cd_material_exame = coalesce(cd_material_exame_p, cd_material_exame);

	if (ie_opcao_p = 1) then
	   ds_retorno_w := nr_sequencia_w;
	elsif (ie_opcao_p = 2) then
	   ds_retorno_w := cd_material_exame_w;
	elsif (ie_opcao_p = 3) then
	   ds_retorno_w := ds_material_exame_w;
	end if;
end if;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
