CREATE OR REPLACE FUNCTION obter_material_informacao ( cd_material_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 			varchar(2000);
ie_dias_util_medic_w		varchar(1);
cd_unidade_medida_consumo_w	varchar(30);
ie_via_aplicacao_w			varchar(5);
ie_tipo_material_w			varchar(3);
ie_bomba_infusao_w		varchar(1);
cd_estabelecimento_w		smallint := wheb_usuario_pck.get_cd_estabelecimento;


BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	begin
	select	ie_dias_util_medic,
		substr(obter_dados_material_estab(cd_material,cd_estabelecimento_w,'UMS'),1,30) cd_unidade_medida_consumo,
		ie_via_aplicacao,
		ie_tipo_material,
		ie_bomba_infusao
	into STRICT	ie_dias_util_medic_w,
		cd_unidade_medida_consumo_w,
		ie_via_aplicacao_w,
		ie_tipo_material_w,
		ie_bomba_infusao_w
	from	material
	where	cd_material = cd_material_p;

	ds_retorno_w := ie_dias_util_medic_w || ';' ||
			cd_unidade_medida_consumo_w || ';' ||
			ie_via_aplicacao_w || ';' ||
			ie_tipo_material_w || ';' ||
			ie_bomba_infusao_w;

	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
