CREATE OR REPLACE FUNCTION obter_material_min_prioridade ( cd_exame_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_material_w			bigint;


BEGIN

SELECT 	MAX(nr_seq_material)
INTO STRICT	nr_seq_material_w
FROM  	exame_lab_material d,
	exame_laboratorio e
WHERE 	d.nr_seq_exame = e.nr_seq_exame
AND	e.cd_exame = cd_exame_p
AND	d.ie_prioridade =
	(SELECT MIN(a.ie_prioridade)
	 FROM  	exame_lab_material a,
		exame_laboratorio b
	 WHERE 	a.nr_seq_exame = b.nr_seq_exame
	 AND	b.cd_exame = cd_exame_p);

RETURN	nr_seq_material_w;

END;
