SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_material_reval_nr_seq (nr_seq_lote_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w				varchar(4000);
nr_seq_mat_hor_w    prescr_mat_hor.nr_sequencia%type;


BEGIN

ds_retorno_w := '';

select max(NR_SEQUENCIA)
into STRICT nr_seq_mat_hor_w
from prescr_mat_hor 
where NR_SEQ_LOTE = nr_seq_lote_p
and CD_MATERIAL = cd_material_p;

select	b.nr_seq_mat_cpoe nr_seq_mat_cpoe
into STRICT ds_retorno_w
from	prescr_mat_hor a,
    prescr_material b
where	a.nr_prescricao = b.nr_prescricao
and		a.nr_seq_material = b.nr_sequencia
and		a.NR_SEQUENCIA = nr_seq_mat_hor_w
and		Obter_se_horario_liberado(a.dt_lib_horario, a.dt_horario) = 'S';

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
