CREATE OR REPLACE FUNCTION obter_material_sistema_ant ( cd_sistema_ant_p text) RETURNS bigint AS $body$
DECLARE


cd_material_w		integer;
cd_estabelecimento_w	smallint := wheb_usuario_pck.get_cd_estabelecimento;


BEGIN

if (cd_sistema_ant_p IS NOT NULL AND cd_sistema_ant_p::text <> '') then
	select	coalesce(max(cd_material), '0')
	into STRICT	cd_material_w
	from	material
	where	substr(obter_dados_material_estab(cd_material,cd_estabelecimento_w,'CSA'),1,20) = cd_sistema_ant_p;
end if;

RETURN	cd_material_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
