CREATE OR REPLACE FUNCTION obter_mat_agente_anestesico ( nr_seq_agente_p bigint, cd_material_p text, ie_considera_mat_p text) RETURNS bigint AS $body$
DECLARE


ie_via_aplicacao_w	varchar(5);
qtde_agente_anest_w	integer;
cd_material_w		integer := null;


BEGIN
if (ie_considera_mat_p = 'S') then
	begin
	select	coalesce(max(ie_via_aplicacao),'0')
	into STRICT 	ie_via_aplicacao_w
	from	agente_anest_material
	where	nr_seq_agente 	= nr_seq_agente_p
	and	cd_material in (cd_material_p);
	end;
else
	begin
	select	coalesce(max(ie_via_aplicacao),'0')
	into STRICT 	ie_via_aplicacao_w
	from	agente_anest_material
	where	nr_seq_agente 	= nr_seq_agente_p;
	end;
end if;

select	count(*)
into STRICT 	qtde_agente_anest_w
from 	agente_anestesico
where 	nr_sequencia = nr_seq_agente_p
and 	ie_tipo in (1,3);

if (ie_via_aplicacao_w = '0') and (qtde_agente_anest_w > 0) then
	begin
	if (ie_considera_mat_p = 'S') then
      		select  coalesce(max(cd_material),0)
      		into STRICT	cd_material_w
      		from	agente_anest_material
      		where	nr_seq_agente = nr_seq_agente_p
      		and	cd_material in (cd_material_p);
   	else
      		select  coalesce(max(cd_material),0)
      		into STRICT	cd_material_w
      		from	agente_anest_material
      		where	nr_seq_agente = nr_seq_agente_p;
   	end if;
	end;
end if;

return	cd_material_w;

end;
$body$
LANGUAGE PLPGSQL
