CREATE OR REPLACE FUNCTION obter_mat_comercial_rel (cd_material_p bigint) RETURNS varchar AS $body$
DECLARE



ds_material_w		varchar(255);
ds_retorno_w		varchar(255) := '';
ie_tipo_material_w	bigint;


BEGIN

select 	max(ie_tipo_material)
into STRICT   	ie_tipo_material_w
from   	material
where 	cd_material = cd_material_p;

	if (ie_tipo_material_w = 3) then

	    ds_retorno_w := Obter_Mat_Comercial(cd_material_p,'D');

	else

		select	substr(max(ds_material),1,255)
		into STRICT	ds_material_w
		from	material
		where	cd_material	= cd_material_p;

		ds_retorno_w := ds_material_w;
	end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
