CREATE OR REPLACE FUNCTION obter_mat_exame_lab_prescr ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, ie_opcao_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_material_w	bigint;


BEGIN


if (ie_opcao_p = 0) then
	select	coalesce(max(d.nr_seq_material),null)
	into STRICT	nr_seq_material_w
	from	exame_lab_result_item d,
		exame_lab_resultado c
	where	c.nr_seq_resultado	= d.nr_seq_resultado
	and	(d.dt_aprovacao IS NOT NULL AND d.dt_aprovacao::text <> '')
	and	(d.nr_seq_material IS NOT NULL AND d.nr_seq_material::text <> '')
	and	c.nr_prescricao		= nr_prescricao_p
	and	d.nr_seq_prescr		= nr_seq_prescr_p;
else
	select	coalesce(max(d.nr_seq_material),null)
	into STRICT	nr_seq_material_w
	from	exame_lab_result_item d,
		exame_lab_resultado c
	where	c.nr_seq_resultado	= d.nr_seq_resultado
	and	(d.nr_seq_material IS NOT NULL AND d.nr_seq_material::text <> '')
	and	c.nr_prescricao		= nr_prescricao_p
	and	d.nr_seq_prescr		= nr_seq_prescr_p;

	if (coalesce(nr_seq_material_w::text, '') = '') then
		select	b.nr_sequencia
		into STRICT	nr_seq_material_w
		from	material_exame_lab b,
			prescr_procedimento a
		where a.cd_material_exame	= b.cd_material_exame
		  and a.nr_prescricao		= nr_prescricao_p
		  and a.nr_sequencia		= nr_seq_prescR_p;
	end if;
end if;

return nr_seq_material_w;

end;
