SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_mat_exame_selecionado ( nr_sequencia_p bigint, ds_lista_mat_proced_p text, nr_seq_rotina_p bigint) RETURNS varchar AS $body$
DECLARE


ds_lista_mat_procedi_w		varchar(4000) := '';
tam_lista_w			bigint;
ie_pos_mat_virgula_w		integer;
ds_lista_mat_rotina_w		varchar(255);
ie_pos_espaco_mat_w		integer;
nr_seq_lista_mat_w		varchar(10);
tam_lista2_w			bigint;
ie_pos_mat_virgula2_w		integer;
ie_tipo_lista_w			varchar(1);
ds_lista_mat_proc_w		varchar(255);
ds_listacur_mat_w		varchar(10);
ds_lista_lab_w			varchar(255);
ie_material_w			varchar(50);


ds_lista_mat_num_rotina_w	varchar(25);
ie_pos_mat_L_w			integer;
nr_num_rotina_w			varchar(25);
ie_pos_espaco_mat2_w		integer;


BEGIN
begin
IF (ds_lista_mat_proced_p IS NOT NULL AND ds_lista_mat_proced_p::text <> '') THEN

	ds_lista_mat_procedi_w := ds_lista_mat_proced_p;


	WHILE (ds_lista_mat_procedi_w IS NOT NULL AND ds_lista_mat_procedi_w::text <> '') LOOP
		BEGIN
		tam_lista_w					:= LENGTH(ds_lista_mat_procedi_w);
		ie_pos_mat_virgula_w		:= position(';' in ds_lista_mat_procedi_w);
		ds_lista_mat_rotina_w		:= SUBSTR(ds_lista_mat_procedi_w,1,(ie_pos_mat_virgula_w));
		ie_pos_mat_L_w 				:= position('--L' in ds_lista_mat_rotina_w);
		ie_pos_espaco_mat_w			:= position('*' in ds_lista_mat_rotina_w);
		ds_lista_mat_num_rotina_w	:= substr(ds_lista_mat_rotina_w,(ie_pos_mat_L_w+3),(ie_pos_espaco_mat_w ));
		ie_pos_espaco_mat2_w		:= position('*' in ds_lista_mat_num_rotina_w);
		nr_num_rotina_w				:= substr(ds_lista_mat_rotina_w,(ie_pos_mat_L_w+3),(ie_pos_espaco_mat2_w - 1));
		nr_seq_lista_mat_w			:= SUBSTR(ds_lista_mat_rotina_w,1,(ie_pos_espaco_mat_w - (ie_pos_espaco_mat_w - (ie_pos_mat_L_w - 1) )));
		ds_lista_mat_rotina_w		:= SUBSTR(ds_lista_mat_rotina_w,ie_pos_espaco_mat_w + 1,(ie_pos_mat_virgula_w - (ie_pos_espaco_mat_w + 1)));


		IF	((nr_sequencia_p = (nr_seq_lista_mat_w)::numeric )and (nr_seq_rotina_p = (nr_num_rotina_w)::numeric )) THEN
		--WHILE ds_lista_mat_rotina_w is not null LOOP
			BEGIN
			tam_lista2_w			:= LENGTH(ds_lista_mat_rotina_w);
			ie_pos_mat_virgula2_w		:= position(',' in ds_lista_mat_rotina_w);
			if (ie_pos_mat_virgula2_w = 0) then
				ds_lista_mat_rotina_w := ds_lista_mat_rotina_w || ',';
				ie_pos_mat_virgula2_w := position(',' in ds_lista_mat_rotina_w);
			end if;
			nr_seq_lista_mat_w		:= SUBSTR(ds_lista_mat_rotina_w,1,(ie_pos_mat_virgula2_w - 1));

			ie_tipo_lista_w			:= SUBSTR(ds_lista_mat_rotina_w,1,1);

				IF (ie_tipo_lista_w = 'L') THEN
					IF (coalesce(ds_lista_lab_w::text, '') = '') THEN
						ds_lista_lab_w := nr_seq_lista_mat_w;
					ELSE
						ds_lista_lab_w := ds_lista_lab_w||','||nr_seq_lista_mat_w;
					END IF;
					ie_material_w := ds_lista_lab_w;

				ELSE
					IF (coalesce(ds_lista_mat_proc_w::text, '') = '') THEN
						ds_lista_mat_proc_w := nr_seq_lista_mat_w;
					ELSE
						ds_lista_mat_proc_w := ds_lista_mat_proc_w||','||nr_seq_lista_mat_w;
					END IF;
					ie_material_w := ds_lista_mat_proc_w;
				END IF;

			ds_lista_mat_rotina_w	:= SUBSTR(ds_lista_mat_rotina_w,(ie_pos_mat_virgula2_w + 1),tam_lista2_w);
			END;
		--END LOOP;
		end if;

		ds_lista_mat_procedi_w	:= SUBSTR(ds_lista_mat_procedi_w,(ie_pos_mat_virgula_w + 1),tam_lista_w);

		END;
	END LOOP;
END IF;
exception
	when others then
		ie_material_w := null;
end;

return	ie_material_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
