CREATE OR REPLACE FUNCTION obter_mat_generico ( cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


cd_material_w	integer := 0;
ie_eh_generico_w	varchar(1);

BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_eh_generico_w
from	material
where	cd_material_generico = cd_material_p
and		coalesce(ie_situacao,'A') = 'A';

if (ie_eh_generico_w = 'N') then

	select	coalesce(max(cd_material_generico),cd_material_p)
	into STRICT	cd_material_w
	from	material
	where	cd_material = cd_material_p
	and		coalesce(ie_situacao,'A') = 'A';
else
	cd_material_w := cd_material_p;
end if;

return	cd_material_w;

end;
$body$
