CREATE OR REPLACE FUNCTION obter_mat_kit_avulso ( nr_seq_kit_estoque_p bigint, nr_seq_reg_kit_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_gerado_barras_w	varchar(1) := 'N';


BEGIN

if (coalesce(nr_seq_kit_estoque_p,0) = 0 ) then
	begin
	select 	max(CASE WHEN coalesce(a.ie_gerado_barras,'N')='S' THEN  'S'  ELSE 'N' END )
	into STRICT	ie_gerado_barras_w
	from   	kit_estoque_comp_avulso a
	where 	a.cd_material = cd_material_p
	and	a.nr_seq_reg_kit = nr_seq_reg_kit_p;
	end;
end if;

return	ie_gerado_barras_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
