SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_mat_med_assoc_ccg ( nr_prescricao_p bigint, nr_seq_item_p bigint, ie_exibe_dose_p text) RETURNS varchar AS $body$
DECLARE


qt_dose_w		double precision;
cd_um_dose_w		varchar(30);
ds_matmed_w		varchar(60);
ds_associados_w		varchar(2000) := null;
ie_via_aplicacao_w	varchar(40);

c01 CURSOR FOR
SELECT	distinct
	substr(x.ds_material,1,60) ds_componente,
	coalesce(CASE WHEN obter_se_ctrl_glic_proc(c.nr_seq_proc_interno)='N' THEN  a.qt_dose  ELSE b.qt_dose END ,a.qt_dose),
	a.cd_unidade_medida_dose,
	CASE WHEN coalesce(a.ie_via_aplicacao::text, '') = '' THEN null  ELSE ' - ' || a.ie_via_aplicacao END ||CASE WHEN coalesce(a.dt_suspensao::text, '') = '' THEN  ''  ELSE ' - SUSPENSO' END
from	material x,
	prescr_mat_hor b,
	prescr_procedimento c,
	prescr_material a
where	a.nr_prescricao 	= c.nr_prescricao
and	a.nr_sequencia_proc 	= c.nr_sequencia
and	x.cd_material		= a.cd_material
and	a.nr_prescricao 	= b.nr_prescricao
and	a.nr_sequencia 		= b.nr_seq_material
and	c.nr_prescricao 	= nr_prescricao_p
and	c.nr_sequencia	 	= nr_seq_item_p
and	(c.nr_seq_proc_interno IS NOT NULL AND c.nr_seq_proc_interno::text <> '')
and	a.ie_agrupador 		= 5
and	Obter_se_horario_liberado(b.dt_lib_horario, b.dt_horario) = 'S'
order by 1;


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') then
	open c01;
	loop
	fetch c01 into	ds_matmed_w,
			qt_dose_w,
			cd_um_dose_w,
			ie_via_aplicacao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
			if (coalesce(ds_associados_w::text, '') = '') and (length(qt_dose_w || ' - ' || cd_um_dose_w || ' - ' || ds_matmed_w || ie_via_aplicacao_w) < 2000) then
				ds_associados_w := qt_dose_w || ' - ' || cd_um_dose_w || ' - ' || ds_matmed_w || ie_via_aplicacao_w;
			elsif (length(ds_associados_w) < 2000) and (length(ds_associados_w || ',' || chr(10) || qt_dose_w || ' - ' || cd_um_dose_w || ' - ' || ds_matmed_w || ie_via_aplicacao_w) < 2000) then
				ds_associados_w := ds_associados_w || ',' || chr(10) || qt_dose_w || ' - ' || cd_um_dose_w || ' - ' || ds_matmed_w || ie_via_aplicacao_w;
			end if;

		end;
	end loop;
	close c01;
end if;

return ds_associados_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
