CREATE OR REPLACE FUNCTION obter_mat_med_assoc_pe_proc (nr_prescricao_p bigint, nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE


qt_dose_w		double precision;
cd_um_dose_w		varchar(30);
ds_matmed_w		varchar(40);
ds_associados_w		varchar(4000) := null;

c01 CURSOR FOR
SELECT	substr(obter_desc_material(cd_material),1,40) ds_componente,
	qt_dose,
	cd_unidade_medida_dose
from	prescr_material
where	nr_prescricao = nr_prescricao_p
and	nr_seq_pe_proc = nr_seq_item_p
and	ie_agrupador in (1,2)
order by
	1;


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') then
	open c01;
	loop
	fetch c01 into
		ds_matmed_w,
		qt_dose_w,
		cd_um_dose_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if (coalesce(ds_associados_w::text, '') = '') then
			ds_associados_w := qt_dose_w || ' - ' || cd_um_dose_w || ' - ' || ds_matmed_w;
		elsif (ds_associados_w IS NOT NULL AND ds_associados_w::text <> '') then
			ds_associados_w := ds_associados_w || chr(10) || qt_dose_w || ' - ' || cd_um_dose_w || ' - ' || ds_matmed_w;
		end if;
		end;
	end loop;
	close c01;
end if;

return ds_associados_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
